import React, { useRef, useState, useCallback } from 'react';
import { useContext } from 'react';
import { useEffect } from 'react';
import {
  GoogleMap,
  withScriptjs,
  withGoogleMap,
  Marker,
} from 'react-google-maps';
import mapDetailContext from './PlaceAutoComplete'




function Map() {

  const context = useContext(mapDetailContext);
  console.log("ðŸš€ ~ file: Map.js:17 ~ Map ~ context", context)




  const [center, setCenter] = useState({
    lat: 23.0166287,
    lng: 72.5199797,
  });

  const refMap = useRef(null);


  const handleBoundsChanged = () => {
    const mapCenter = refMap.current.getCenter(); //get map center
    console.log("lat", mapCenter.lat(), "lng", mapCenter.lng() );
    setCenter(mapCenter)

  };
  return (
    <>
      <GoogleMap
        ref={refMap}
        defaultZoom={15}
        defaultCenter={{ lat: 23.0166287, lng: 72.5199797 }}
        onBoundsChanged={handleBoundsChanged}
      >
        <Marker position={center} />
      </GoogleMap>
    </>
  );


  <WrappedMap
    googleMapURL={`https://maps.googleapis.com/maps/api/js?key=AIzaSyDcjtGb2jSVKXsUjxVAcJx6hboHbUe6fqI`}
    loadingElement={<div style={{ height: `100%` }} />}
    containerElement={<div style={{ height: `220px` }} />}
    mapElement={<div style={{ height: `100%` }} />}
  />;

}

export default withScriptjs(withGoogleMap(Map));
