import React, { useRef, useState, useCallback } from 'react';
import {
  GoogleMap,
  withScriptjs,
  withGoogleMap,
  Marker,
} from 'react-google-maps';

function Map() {
  const lat = localStorage.getItem("lat")
  console.log("ðŸš€ ~ file: Map.js:11 ~ Map ~ lat", lat)
  const lag = localStorage.getItem("lag");
  console.log("ðŸš€ ~ file: Map.js:13 ~ Map ~ lag", lag)
  const [center, setCenter] = useState({
    lat: lat,
    lng: lag,
  });
  const refMap = useRef(null);

  const handleBoundsChanged = () => {
    const mapCenter = refMap.current.getCenter(); //get map center
    console.log("lat", mapCenter.lat(), "lag", mapCenter.lng() );
    setCenter(mapCenter)
  };

  return (
    <>
      <GoogleMap
        ref={refMap}
        defaultZoom={15}
        defaultCenter={{ lat: 23.017167895979963, lng: 72.5546501178857 }}
        onBoundsChanged={handleBoundsChanged}
      >
        <Marker position={{ lat: Number(lat), lng: Number(lag) }} />
      </GoogleMap>
    </>
  );

}

export default withScriptjs(withGoogleMap(Map));
