import React, { useRef, useState, useCallback } from 'react';
import { useEffect } from 'react';
import {
  GoogleMap,
  withScriptjs,
  withGoogleMap,
  Marker,
} from 'react-google-maps';
import { values } from "./PlaceAutoComplete";

