import React, { useRef, useState, useCallback } from "react";
import { useEffect } from "react";
import {
  GoogleMap,
  withScriptjs,
  withGoogleMap,
  Marker,
} from "react-google-maps";
import PlacesAutocomplete, {
  geocodeByAddress,
  getLatLng,
} from "react-places-autocomplete";

function Map() {
  const [cordinates, setCordinates] = useState({
    lat: 23.0166287,
    lng: 72.5199797,
  });
  console.log(
    "ðŸš€ ~ file: placeAutocomplete.js:18 ~ placeAutocomplete ~ cordinates",
    cordinates
  );
  const [address, setAddress] = useState("");
  const handleChange = async (value) => {
    const result = await geocodeByAddress(value);
    const ll = await getLatLng(result[0]);

    setAddress(value);
    // console.log("ðŸš€ ~ file: index.js:22 ~ handleSelect ~ ll", ll);
    setCordinates(ll);
    console.log("c1", cordinates);
    console.log("l1", ll);
    console.log("c2", cordinates.lat);
    console.log("l2", ll.lat);
  };

  const [center, setCenter] = useState({
    lat: 23.0166287,
    lng: 72.5199797,
  });
  console.log("ðŸš€ ~ file: Map.js:35 ~ Map ~ center", center);
  const refMap = useRef(null);

  const handleBoundsChanged = () => {
    const mapCenter = refMap.current.getCenter(); //get map center
    console.log("lat", mapCenter.lat(), "lng", mapCenter.lng());
    setCenter(mapCenter);
  };
  return (
    <>
      <PlacesAutocomplete
        value={address}
        onChange={setAddress}
        onSelect={handleChange}
      >
        {({ getInputProps, suggestions, getSuggestionItemProps, loading }) => (
          <div>
            <input
              {...getInputProps({
                placeholder: "Search Places ...",
                className: "location-search-input",
              })}
            />
            <div className="autocomplete-dropdown-container">
              {loading && <div>Loading...</div>}
              {suggestions.map((suggestion) => {
                const style = {
                  backgroundColor: suggestion.active ? "#41b6e6" : "#fff",
                  borderStyle: "hidden",
                  color: "black",
                };
                return (
                  <div key={suggestion.index}>
                    <div
                      className="suggestionInput"
                      {...getSuggestionItemProps(suggestion, {
                        style,
                      })}
                    >
                      {console.log("dcisjkhckdc",suggestion.description)}
                    </div>

                  </div>
                );
              })}
            </div>
          </div>
        )}
      </PlacesAutocomplete>

      <GoogleMap
        ref={refMap}
        defaultZoom={15}
        defaultCenter={{ lat: 23.0166287, lng: 72.5199797 }}
        onBoundsChanged={handleBoundsChanged}
      >
        <Marker position={center} />
      </GoogleMap>
    </>
  );
}

export default withScriptjs(withGoogleMap(Map));
